INSERT INTO `syllabus` VALUES (1,'Dự án phát triển phần mềm','Application development project','SWP391',3,'Bachelor','Study hour (150h) = 45h contact hours + 105h self-study','PRJ301 ','This course focuses on designing, developing, and integrating the basic Web-based management system (as assigned/agreed by the teacher) using Java Web (JSP, Servlet, JDBC) or .NET (ASP.NET, ADO.NET) technologies . Students are required to build the system with other 3-5 team members as appointed by the teacher.\nAfter the course, students will be able to achieve Java web programing proficiency with the following skills by practicing with other members in the assigned team\n- Proficiency in common web techniques and basic Java Web skills (JSP, Servlet, JDBC) or .NET Web skills (ASP.NET, ADO.NET)\n- Proficiency in front end skills (HTML, CSS, JS) require for industry\n- Proficiency in SQL skills required for industry\n- Analyze & design the solution following the object oriented models\n- Co-ordinate with the team to complete the works in the form of a project','- Register the programming language to be used (Java or .NET),\n- Build the team & select/define the project topic with arrangement/guide from the teacher\n- Select the team leader and using the FPT account (@fpt.edu.vn account) to setup/configure the project managemernt environment (sources, tasks, issues, milestones) on the GitLab & project documents management on the OneDrive.\n- Get the detailed assignment from the teacher, plan the project tasks, and execute the project accordingly\n- Students must attend at least 80% of contact slots in order to be accepted to the final presentation.\n- Student is responsible to do all exercises given by the teacher in class or at home and submit on time\n- Constantly follow announcements on intranet/LMS at http://lms.fpt.edu.vn for up-to-date course information regarding assignment submission and feedback on assignments and project work.\n- Using the GitLab to collaborate in every phase of the project by all the team members','GitLab (https://gitlab.com/), One Drive\nFor Java project\n• Java JDK version 1.6 or higher, NetBeans 13\n• MySQL Server (v8.0.23 or higher), MySQL Workbench;\nFor .NET project\n• Microsoft Internet Information Services server (come with Microsoft Windows)\n• Microsoft Visual Studio version 2015 or higher (for .NET project)','10','1189/QÐ-ÐHFPT',1,'1) On-going Assessment\n- On-going Assessment 1: 20%\n- On-going Assessment 2: 20%\n- On-going Assessment 3: 20%\n2) Final Project Presentation: 40%\n\nTo be selected to the Final Presentation, each student needs:\n* On-going grade assessment >=5/10\n* No cheating during the project progress',5,1),(2,'Lập trình hướng đối tượng','Object-Oriented Programming','PRO192',NULL,'Bachelor',NULL,'PRF192',NULL,NULL,NULL,NULL,'1189/QÐ-ÐHFPT',NULL,NULL,NULL,NULL),(3,'Các hệ cơ sở dữ liệu','Introduction to Databases','DBI202',NULL,NULL,NULL,'',NULL,NULL,NULL,NULL,'1189/QÐ-ÐHFPT',NULL,NULL,NULL,NULL),(4,'Cơ sở lập trình','Programming Fundamentals','PRF192',NULL,NULL,NULL,'None',NULL,NULL,NULL,NULL,'1189/QÐ-ÐHFPT',NULL,NULL,NULL,NULL),(7,'Phát triển ứng dụng Java web','Java Web Application Development','PRJ301',NULL,NULL,NULL,'DBI202, PRO192',NULL,NULL,NULL,NULL,'1189/QÐ-ÐHFPT',NULL,NULL,NULL,NULL);
